INSERT INTO camp_types (title, description)
VALUES
  (
    '{"ru": "Исправительно-трудовые лагеря","en": "Corrective-labor camps","de": ""}',
    '{"ru": "Появились в 1930 году и действовали вплоть до 50-х годов. В структуру ИТЛ входило лагерное управление вместе с подчиненными ему лагерными отделениями, а также прочие объекты, составлявшие хозяйственную базу лагеря.","en": "Corrective-labor camps (ITLs) appeared in 1930 and operated until the 1950s. The ITL structure included camp administration, camp branches and other economic facilities.","de": ""}'
  ),
  (
    '{"ru": "Особые лагеря","en": "Special camps","de": ""}',
    '{"ru": "Действовали с 1948 по 1956 год. Были созданы на базе каторжных отделений и отличались от исправительно-трудовых лагерей более суровыми условиями труда и содержания. Всего было организовано 12 особых лагерей.","en": "Special camps operated from 1948 to 1956. They were set up on the basis of hard-labor branches but were distinguished from corrective-labor camps by harder work conditions and imprisonment. There were 12 special camps in total.","de": ""}'
  ),
  (
    '{"ru": "Спецлагеря","en": "NKVD special camps","de": ""}',
    '{"ru": "Организованы на территории оккупированной Восточной Германии в 1945 году для интернирования местного населения (рядовых и офицеров немецкой армии, сотрудников гестапо и надзирателей концлагерей). Спецлагеря были обустроены в помещениях бывших нацистских лагерей и подчинялись НКВД (с 1948 года — ГУЛАГу). Ликвидированы в 1950 году.","en": "NKVD special camps were established in the Soviet-occupied Eastern part of Germany in 1945 for interning the local population. The special camps were arranged in the former Nazi camps. They were liquidated in 1950.","de": ""}'
  ),
  (
    '{"ru": "Проверочно-фильтрационные лагеря","en": "Screening and filtration camps","de": ""}',
    '{"ru": "Организованы НКВД на территории СССР в декабре 1941 года для временного содержания бывших в плену советских военнослужащих и гражданского населения, оказавшегося в зоне оккупации. По окончании проверки часть контингента возвращалась на фронт, а лица, обвиненные в шпионаже и измене, направлялись в лагеря ГУЛАГа.","en": "Screening and filtrations camps were established in the USSR in December 1941 for temporary confinement of Soviet soldiers and the civil population from the Soviet territories occupied by Nazi Germany.","de": ""}'
  ),
  (
    '{"ru": "Спецпоселения","en": "Special settlements","de": ""}',
    '{"ru": "Возникли в начале 30-х годов в отдаленных областях СССР, таких как Средняя Азия, Сибирь, Урал и Крайний Север. В результате административной ссылки, высылки или депортации в них оказывались тысячи советских граждан.","en": "Special settlements first appeared in early 1930s in far-away areas of the USSR, such as Central Asia, Siberia, the Urals and the Extreme North. Thousands of its settlers were forcibly exiled to these places from other Soviet regions.","de": ""}'
  ),
  (
    '{"ru": "Лагерные пункты","en": "Camp points","de": ""}',
    '{"ru": "Входили в состав лагерных отделений и представляли собой низовые подразделения исправительно-трудового лагеря, предназначенные для выполнения конкретных производственных задач. Они располагались в непосредственной близости от места проведения работ, но могли отстоять на сотни километров от управления того исправительно-трудового лагеря, которому они подчинялись.","en": "Low-level corrective-labor units designed to fulfill specific production tasks.","de": ""}'
  );
